Events.OnGlobalStartDiagnostics:
(block (
	(testItem (itmCreate &itLaserCannon; 1))
	)
	;	Unit Tests for Functions

	(diagEval (list
		{	test:'(setq testItem1 (itmCreate &itLaserCannon; 1))			result:'ignore	}
		{	test:'(itmGetType testItem1)									result:&itLaserCannon;	}
		{	test:'(setq testItem2 (itmCreate &itLaserCannon; 1))			result:'ignore	}

		;	itmIsEqual -----------------------------------------

		{	test:'(itmIsEqual testItem1 testItem2)							result:True	}
		{	test:'(itmIsEqual testItem1
					(itmCreate &itLaserCannon; 10)
					)														result:True	}
		{	test:'(itmIsEqual testItem1
					(itmCreate &itBallistaCannon; 1)
					)														result:Nil	}
		{	test:'(itmIsEqual 
					(itmSet@ testItem1 'damaged True)
					testItem2
					)														result:Nil	}
		{	test:'(itmIsEqual 
					(itmSet@ testItem1 'charges 10)
					testItem2
					)														result:Nil	}
		{	test:'(itmIsEqual 
					(itmSetEnhanced testItem1 0x102)
					testItem2
					)														result:Nil	}
		{	test:'(itmIsEqual 
					(itmSetData testItem1 'test 10)
					testItem2
					)														result:Nil	}

		{	test:'(itmIsEqual 
					(itmSet@ testItem1 'charges 10)
					testItem2
					'ignoreCharges
					)														result:True	}
		{	test:'(itmIsEqual 
					(itmSet@ testItem1 'charges 10)
					testItem2
					'ignoreEnhancements
					)														result:Nil	}
		{	test:'(itmIsEqual 
					(itmSetEnhanced testItem1 0x102)
					testItem2
					'ignoreEnhancements
					)														result:True	}
		{	test:'(itmIsEqual 
					(itmSetEnhanced testItem1 0x102)
					testItem2
					'ignoreCharges
					)														result:Nil	}
		{	test:'(itmIsEqual 
					(itmSetEnhanced testItem1 0x102)
					testItem2
					'(ignoreCharges ignoreData ignoreEnhancements)
					)														result:True	}
		{	test:'(itmIsEqual 
					(itmSetData testItem1 'test 10)
					testItem2
					'ignoreData
					)														result:True	}
		{	test:'(itmIsEqual 
					(itmSetData testItem1 'test 10)
					testItem2
					'ignoreCharges
					)														result:Nil	}

		;	itmMatches -----------------------------------------

		{	test:'(itmMatches testItem1 "w")								result:True	}
		{	test:'(itmMatches testItem1 "*~s")								result:True	}
		{	test:'(itmMatches testItem1 "*~w")								result:Nil	}
		{	test:'(itmMatches testItem1 "w +energyWeapon;")					result:True	}
		{	test:'(itmMatches testItem1 "w -launcher;")						result:True	}
		{	test:'(itmMatches testItem1 "w =1;")							result:True	}
		{	test:'(itmMatches testItem1 "w L:1-4;")							result:True	}
		{	test:'(itmMatches testItem1 "w L:2-5;")							result:Nil	}
		{	test:'(itmMatches testItem1 "w +unid:&itLaserCannon;;")			result:True	}

		{	test:'(itmMatches testItem1 "w +property:omnidirectional;")		result:Nil	}
		{	test:'(itmMatches testItem1 "w +property:deviceSlots;")			result:True	}
		{	test:'(itmMatches testItem1 "w +property:deviceSlots=1;")		result:True	}
		{	test:'(itmMatches testItem1 "w +property:deviceSlots=2;")		result:Nil	}
		{	test:'(itmMatches testItem1 "w +property:deviceSlots>1;")	result:Nil	}
		{	test:'(itmMatches testItem1 "w +property:deviceSlots>=1;")	result:True	}
		{	test:'(itmMatches testItem1 "w +property:deviceSlots<1;")	result:Nil	}
		{	test:'(itmMatches testItem1 "w +property:deviceSlots<=1;")	result:True	}
		{	test:'(itmMatches testItem1 "w +property:deviceSlots!=1;")		result:Nil	}
		{	test:'(itmMatches testItem1 "w +property:deviceSlots!=3;")		result:True	}

		;	Test Item Data -------------------------------------

		{	test:'(setq markerObj 
					(sysCreateMarker "Test" Nil &svPlayer;)
					)
																			result:'ignore	}

		{	test:'(objAddItem markerObj &itLaserCannon; 5)					result:(itmCreate &itLaserCannon; 5)	}
		{	test:'(objGetItems markerObj "*")								result:(list (itmCreate &itLaserCannon; 5))	}

		;	NOTE: If we don't specify a count, we only modify 1 item, even if the
		;	item has a count.

		{	test:'(objSetItemData markerObj 
					(itmCreate &itLaserCannon; 5)
					'foo
					'bar
					)														result:(itmSetData (itmCreate &itLaserCannon; 1) 'foo 'bar)	}
		{	test:'(objGetItems markerObj "*")								result:(list (itmCreate &itLaserCannon; 4) (itmSetData (itmCreate &itLaserCannon; 1) 'foo 'bar))	}

		;	Reset ----------------------------------------------

		{	test:'(enum (objGetItems markerObj "*") theItem (objRemoveItem markerObj theItem))
																			result:True	}
		{	test:'(objAddItem markerObj &itLaserCannon; 5)					result:(itmCreate &itLaserCannon; 5)	}
		{	test:'(objGetItems markerObj "*")								result:(list (itmCreate &itLaserCannon; 5))	}
		;	----------------------------------------------------

		;	Try setting all 5 items

		{	test:'(objSetItemData markerObj 
					(itmCreate &itLaserCannon; 1)
					'foo
					'bar
					5
					)														result:(itmSetData (itmCreate &itLaserCannon; 1) 'foo 'bar 5)	}
		{	test:'(objGetItems markerObj "*")								result:(list (itmSetData (itmCreate &itLaserCannon; 5) 'foo 'bar))	}

		;	Reset ----------------------------------------------

		{	test:'(enum (objGetItems markerObj "*") theItem (objRemoveItem markerObj theItem))
																			result:True	}
		{	test:'(objAddItem markerObj &itLaserCannon; 5)					result:(itmCreate &itLaserCannon; 5)	}
		{	test:'(objGetItems markerObj "*")								result:(list (itmCreate &itLaserCannon; 5))	}
		;	----------------------------------------------------

		;	Try setting more than exist; we should still end up 
		;	with only 5.

		{	test:'(objSetItemData markerObj 
					(itmCreate &itLaserCannon; 1)
					'foo
					'bar
					10
					)														result:(itmSetData (itmCreate &itLaserCannon; 1) 'foo 'bar 5)	}
		{	test:'(objGetItems markerObj "*")								result:(list (itmSetData (itmCreate &itLaserCannon; 5) 'foo 'bar))	}

		;	Done

		{	test:'(objDestroy markerObj)									result:'ignore	}
		{	test:'(setq markerObj Nil)										result:'ignore	}

		;	Test Scaled Armor ----------------------------------

		{	test:'(itm@ 
					(itmSet@ (itmCreate &itTestArmorScaled; 1) 'level 5)
					'level
					)														result:5	}
		{	test:'(itm@ 
					(itmSet@ (itmCreate &itTestArmorScaled; 1) 'level 5)
					'hp
					)														result:166	}
		{	test:'(itm@ 
					(itmSet@ (itmCreate &itTestArmorScaled; 1) 'level 5)
					'price
					)														result:4000	}
		{	test:'(itm@ 
					(itmSet@ (itmCreate &itTestArmorScaled; 1) 'level 5)
					'repairCost
