		{	test:'(itm@ 
					(itmSet@ (itmCreate &itTestArmorScaled; 1) 'level 5)
					'repairLevel
					)														result:5	}
		{	test:'(itm@ 
					(itmSet@ (itmCreate &itTestArmorScaled; 1) 'level 5)
					'powerUse
					)														result:40	}
		{	test:'(itm@ 
					(itmSet@ 
						(itmSet@ (itmCreate &itTestArmorScaled; 1) 'hp 50)
						'level 5
						)
					'hp
					)														result:83	}

		;	Test Scaled Weapons --------------------------------

		{	test:'(itm@ 
					(itmSet@ (itmCreate &itTestWeaponScaled; 1) 'level 5)
