	(trData {
		targetName: (objGetName (objGetObjByID (msn@ gSource 'targetID)) 'article)
		payment: (fmtCurrency 'credit (msn@ gSource 'reward))
		})
	)
	(list
		{	descID:'textBriefing1		data:trData	}
		)
	)
