TranscendenceModule.Globals:
(block Nil
	(setq coreCalcLaserColor (lambda (damageHP)
		(switch
			(geq damageHP 15)
				{
					primaryColor: "#D500FF"
					secondaryColor: "#6A00FF"
					}

			(geq damageHP 7)
				{
					primaryColor: "#55FF00"
					secondaryColor: "#2A8000"
					}

			(geq damageHP 4)
				{
					primaryColor: "#FF5500"
					secondaryColor: "#802A00"
					}

			{
				primaryColor: "#FF0000"
				secondaryColor: "#800000"
				}
			)
		))
	)
