Properties.Definition:
(list
	Nil
	{	level:1		xp:0		icon:(resCreateImageDesc &HS_rsSistersRanks; 0 0 256 256) }
	{	level:2		xp:250		icon:(resCreateImageDesc &HS_rsSistersRanks; 256 0 256 256) }
	{	level:3		xp:500		icon:(resCreateImageDesc &HS_rsSistersRanks; 512 0 256 256) }
	{	level:4		xp:1000		icon:(resCreateImageDesc &HS_rsSistersRanks; 0 256 256 256) }
	{	level:5		xp:2000		icon:(resCreateImageDesc &HS_rsSistersRanks; 256 256 256 256) }
	{	level:6		xp:4000		icon:(resCreateImageDesc &HS_rsSistersRanks; 512 256 256 256) }
	)

Events.GetGlobalAchievements:
(block (
	(level (typ@ gType 'level))
	(psionicsOffered (typGetGlobalData &HS_stDomina; "psionicsOffered"))
	(slavesOffered (typGetGlobalData &HS_stDomina; "slavesOffered"))

	theList 
	)
	(if (and level (gr level 0))
		(setq theList (list
			(list
				"Domina relationship"
				(fmtNoun (typ@ &RPG_unidPilgrimClass; 'levelNoun) 1 '(capitalize))
				)
			))
		)

	(if (and (gr level 0) 
			(not (typGetGlobalData &HS_stDomina; "invokeCount"))
			)
		(setq theList (append theList (list
			(list "Never invoked powers of Domina" Nil "conducts")
			)))
		)

	(if psionicsOffered
		(setq theList (append theList (list
			(list "Psionics offered to Domina" psionicsOffered "missions & activities")
			)))
		)

	(if slavesOffered
		(setq theList (append theList (list
			(list "Slaves offered to Domina" slavesOffered "missions & activities")
			)))
		)

	theList
	)

Language.Code:
(block (
	(level (typ@ gType 'level))
	(nextLevel (typ@ gType 'nextLevel))
	)
	(if level
		{
			title: (fmtNoun (typ@ gType 'levelNoun) 1 'titleCapitalize)
			largeIcon: (typ@ gType 'levelIcon)

			details: (list
				{
					title: (typTranslate gType 'labelDominaPowers)
					desc: (typTranslate gType 'rtfLevelValue { 
						value: level
						})
					}
				(if nextLevel
					{
						title: (typTranslate gType 'labelProgress {
							nextLevel: (fmtNoun (typTranslate gType 'nounRankPattern { rank:(@ nextLevel 'nextLevel) gender:(plyGetGenome gPlayer) }) 1 'titleCapitalize)
							})
						desc: (typTranslate gType 'rtfValue { 
							value: (cat (@ nextLevel 'progress) "%")
							})
						}
					)
				{
					title: (typTranslate gType 'labelAidBudget)
					desc: (typTranslate gType 'rtfValue {
						value: (fmtCurrency 'credit (typ@ &HS_svSistersOfDomina; 'balance))
						})
					}
				)

			detailsStyle: 'stats
			align: 'bottom
			}
		)
	)
