Properties.Definition:
(list
	Nil
	{	level:1		xp:0	}
	{	level:2		xp:250	}
	{	level:3		xp:500	}
	{	level:4		xp:1000	}
	{	level:5		xp:2000	}
	{	level:6		xp:4000	}
	)

Events.GetGlobalAchievements:
(block (
	(level (typ@ gType 'level))
	(psionicsOffered (typGetGlobalData &stDomina; "psionicsOffered"))
	(slavesOffered (typGetGlobalData &stDomina; "slavesOffered"))

	theList 
	)
	(if (and level (gr level 0))
		(setq theList (list
			(list
				"Domina relationship"
				(fmtNoun (typ@ &unidPilgrimClass; 'rankNoun) 1 '(capitalize))
				)
			))
		)

	(if (and (gr level 0) 
			(not (typGetGlobalData &stDomina; "invokeCount"))
			)
		(setq theList (append theList (list
			(list "Never invoked powers of Domina" Nil "conducts")
			)))
		)

	(if psionicsOffered
		(setq theList (append theList (list
			(list "Psionics offered to Domina" psionicsOffered "missions & activities")
			)))
		)

	(if slavesOffered
		(setq theList (append theList (list
			(list "Slaves offered to Domina" slavesOffered "missions & activities")
			)))
		)

	theList
	)

Language.Code:
(block (
	(level (typ@ gType 'level))
	(nextLevel (typ@ gType 'nextLevel))
	)
	(if level
		{
			title: (fmtNoun (typ@ gType 'rankNoun) 1 'titleCapitalize)
			largeIcon: (typ@ gType 'levelIcon)

			details: (list
				{
					title: (typTranslate gType 'labelDominaConnection)
					desc: (typTranslate gType 'rtfValue { 
						value:(fmtNumber (typ@ gType 'xp))
						})
					}
				(if nextLevel
					{
						title: (typTranslate gType 'labelProgress {
							nextLevel: (fmtNoun (typTranslate gType 'nounRankPattern { rank:(@ nextLevel 'nextLevel) gender:(plyGetGenome gPlayer) }) 1 'titleCapitalize)
							})
						desc: (typTranslate gType 'rtfValue { 
							value: (cat (@ nextLevel 'progress) "%")
							})
						}
					)
				{
					title: (typTranslate gType 'labelSistersAttitude)
					desc: (typTranslate gType 'rtfValue {
						value: (fmtNumber (typ@ gType 'sistersRel))
						})
					}
				)

			detailsStyle: 'stats
			align: 'bottom
			}
		)
	)
