Display.OnDisplayInit:
(map (obj@ gPlayerShip 'squadron) theEntry
	(block (
		(shipObj (@ theEntry 'obj))
		)
		{
			title: (@ theEntry 'name)
			icon: (objGetImageDesc shipObj)

			status: (@ theEntry 'status)
			obj: (@ theEntry 'obj)
			objID: (@ theEntry 'objID)
			type: (@ theEntry 'type)
			}
		)
	)

Default.OnPaneInit:
(block (
	(squadronList (scrGetList gScreen))
	(entry (scrGetListEntry gScreen))
	)
	(switch

		;	If nothing is selected, then we show commands that
		;	apply to the whole squadron.

		(not entry)
			(block (
				(actionPos -1)
				(nextActionPos (lambda () (setq actionPos (+ actionPos 1))))
				(squadronComms (obj@ gPlayerShip 'squadronCommsStatus))

				(deployedCount (map squadronList 'reduceSum theEntry
					(if (= (@ theEntry 'status) 'deployed) 1)
					))
				)
				;	Add squadon orders

				(if (find squadronComms 'msgAttackTarget)
					(scrAddAction gScreen 'msgAttackTarget (nextActionPos) Nil
						(lambda () (objSquadronComms gPlayerShip 'squadron 'msgAttackTarget))
						)
					)

				(if (find squadronComms 'msgBreakAndAttack)
					(scrAddAction gScreen 'msgBreakAndAttack (nextActionPos) Nil
						(lambda () (objSquadronComms gPlayerShip 'squadron 'msgBreakAndAttack))
						)
					)

				(if (find squadronComms 'msgAttackInFormation)
					(scrAddAction gScreen 'msgAttackInFormation (nextActionPos) Nil
						(lambda () (objSquadronComms gPlayerShip 'squadron 'msgAttackInFormation))
						)
					)

				(if (find squadronComms 'msgFormUp)
					(scrAddAction gScreen 'msgFormUp (nextActionPos) Nil
						(lambda () (objSquadronComms gPlayerShip 'squadron 'msgFormUp))
						)
					)

				(if (find squadronComms 'msgSetFormation)
					(block ()
						(scrAddAction gScreen 'msgFormation1 (nextActionPos) Nil
							(lambda () (objSquadronComms gPlayerShip 'squadron 'msgFormationAlpha))
							)
						(scrAddAction gScreen 'msgFormation2 (nextActionPos) Nil
							(lambda () (objSquadronComms gPlayerShip 'squadron 'msgFormationBeta))
							)
						(scrAddAction gScreen 'msgFormation3 (nextActionPos) Nil
							(lambda () (objSquadronComms gPlayerShip 'squadron 'msgFormationGamma))
							)
						)
					)

				(if (find squadronComms 'msgWait)
					(scrAddAction gScreen 'msgWait (nextActionPos) Nil
						(lambda () (objSquadronComms gPlayerShip 'squadron 'msgWait))
						)
					)

				;	Set the descriptor

				(switch
					(gr deployedCount 0)
						(scrSetDescTranslate gScreen 'descSquadronDeployed {
							shipCount: (fmtNoun (scrTranslate gScreen 'nounShip) deployedCount 'countAlways)
							})

					(scrSetDescTranslate gScreen 'descNoSquadron)
					)
				)

		(scrSetDesc gScreen "LATER: " entry)
		)
	)
